import {useState, useEffect} from "react"
import firebase from "../firebaseConnection"
import { useNavigate } from "react-router-dom";

function Singup() {
  const [email, setEmail] =useState("")
  const [senha, setSenha] =useState("")

  const navigate = useNavigate()

  useEffect(() =>{
    async function checkLogin(){ //verificar se o usuario esta logado
      await firebase.auth().onAuthStateChanged((user)=>{
        if(user){
          setUser(true)
          setUserLogged({
            uid: user.uid,
            email: user.email
          })
        }else{
          setUser(false)
          setUserLogged({})
        }
      })
    }

    checkLogin()
  },[])

  async function novoUsuario(){ //criar novo usuario
    await firebase.auth().createUserWithEmailAndPassword(email, senha)
    .then(()=>{
      setEmail("")
      setSenha("")
      navigate("/Home")
    })
    .catch((error) =>{ //erro
      if(error.code === "auth/weak-password"){
        alert("senha muito fraca")
      }else if(error.code === "auth/email-already-in-use"){
        alert("email ja existe")
      }
      else if(error.code === "auth/invalid-email"){
        alert("email invalido")
      }

    })
  }

  return (
    <div className="Box-Login">
      <label>Email</label>
      <input type="text" value={email} onChange={ (e)=> setEmail(e.target.value)}/><br/>
      <label>Senha</label>
      <input type="password" value={senha} onChange={ (e)=> setSenha(e.target.value)}/><br/>
      <button onClick={novoUsuario}>Cadastrar</button><br/>
      <button onClick={() => navigate("/Login")}>Login</button><br/>
    </div>
  );
}

export default Singup;
